changeSet.createView:
select t.thread_id,
       (select m.message_id from message m where m.thread_id = t.thread_id order by m.idate asc limit 1) as initial_message_id, (select m.message_id from message m where m.thread_id = t.thread_id order by m.idate desc limit 1) as latest_message_id
from thread t;
